select STUDENT.* from STUDENT left join MARK on STUDENT.ID = MARK.STUDENT_ID group by STUDENT.ID having avg(MARK.MARK)>8;
select STUDENT.ID, STUDENT.NAME from STUDENT left join MARK on STUDENT.ID = MARK.STUDENT_ID group by STUDENT.ID having min(MARK.MARK)>7;
select STUDENT.ID, STUDENT.NAME from STUDENT left join PAYMENT on STUDENT.ID = PAYMENT.STUDENT_ID where YEAR(PAYMENT.PAYMENT_DATE)=2019 group by STUDENT.ID having count(*)>2;